mapper.select:
SELECT
FROM sys_permission a
WHERE a.id = #{id}

SELECT

FROM sys_permission a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.parent_id = #{parentId}


		AND a.type = #{type}


		AND a.status = #{status}

SELECT

FROM sys_permission a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE sys_permission SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
